// ********************** PUBLIC OPERATIONS ******************************// Construction: // public Face( String inputFile ) 				~ the location of a file as a String// public void drawFace( Graphics g )			~ draws the image input//// ********************** PRIVATE OPERATIONS ******************************//// private void setCoordinates( FileReader file ) ~ associates the coord's with arrays// private int[] getXCoord( String Coord, int pts ) ~ extracts the x coordinates// private int[] getYCoord( String Coord, int pts ) ~ extracts the y coordinates import java.awt.*;import java.util.StringTokenizer;import java.io.*;public class Face {		public static final int STANDARD = 0;	public static final int NEW = 1;		private FaceFile file;		public int[] Lpupilx, Lpupily, Rpupilx, Rpupily;	public int[] Ririsx, Ririsy, Lirisx, Lirisy;	public int[] BLeyelidx,  BLeyelidy,  BReyelidx,  BReyelidy;	public int[] BLeyex,  BLeyey,  BReyex,  BReyey;	public int[] TLeyex,  TLeyey,  TReyex,  TReyey;	public int[] Leyelinex, Leyeliney, Reyelinex, Reyeliney;	public int[] Lnosex,  Lnosey,  Rnosex,  Rnosey;	public int[] Lnostrilx,  Lnostrily,  Rnostrilx,  Rnostrily;	public int[] TLeyebrowx,  TLeyebrowy,  TReyebrowx,  TReyebrowy;	public int[] BLeyebrowx,  BLeyebrowy,  BReyebrowx,  BReyebrowy;	public int[] Tupperlipx,  Tupperlipy, Bupperlipx,  Bupperlipy;	public int[] Tlowerlipx,  Tlowerlipy, Blowerlipx,  Blowerlipy;	public int[] Lfacex,  Lfacey, Rfacex,  Rfacey;	public int[] Learx,  Leary, Rearx,  Reary;	public int[] jawx, jawy, hairlinex, hairliney, topofheadx, topofheady;	public int[] Lcheeklinex,  Lcheekliney, Rcheeklinex,  Rcheekliney;	public int[] Lcheekbonex,  Lcheekboney, Rcheekbonex,  Rcheekboney;	public int[] Lupperliplinex, Lupperlipliney, Rupperliplinex, Rupperlipliney;	public int[] chincleftx,  chinclefty, chinlinex,  chinliney;	 		public Face( int inputFile ) {				if( inputFile == STANDARD )			file = new StandardFace();					else if( inputFile == NEW )			file = new NewFace();					setCoordinates( file );	}		private void setCoordinates( FaceFile file ) {			String S = "";			S = file.readLine();		Lpupilx = getXCoord( S, 2 );		Lpupily = getYCoord( S, 2 );		S = file.readLine();		Rpupilx = getXCoord( S, 2 );		Rpupily = getYCoord( S, 2 );			S = file.readLine();		Lirisx = getXCoord( S, 5 );		Lirisy = getYCoord( S, 5 );		S = file.readLine();		Ririsx = getXCoord( S, 5 );		Ririsy = getYCoord( S, 5 );			S = file.readLine();		BLeyelidx = getXCoord( S, 3 );		BLeyelidy = getYCoord( S, 3 );		S = file.readLine();		BReyelidx = getXCoord( S, 3 );		BReyelidy = getYCoord( S, 3 );			S = file.readLine();		BLeyex = getXCoord( S, 3 );		BLeyey = getYCoord( S, 3 );		S = file.readLine();		BReyex = getXCoord( S, 3 );		BReyey = getYCoord( S, 3 );			S = file.readLine();		TLeyex = getXCoord( S, 3 );		TLeyey = getYCoord( S, 3 );		S = file.readLine();		TReyex = getXCoord( S, 3 );		TReyey = getYCoord( S, 3 );			S = file.readLine();		Leyelinex = getXCoord( S, 3 );		Leyeliney = getYCoord( S, 3 );		S = file.readLine();		Reyelinex = getXCoord( S, 3 );		Reyeliney = getYCoord( S, 3 );			S = file.readLine();		Lnosex = getXCoord( S, 6 );		Lnosey = getYCoord( S, 6 );		S = file.readLine();		Rnosex = getXCoord( S, 6 );		Rnosey = getYCoord( S, 6 );			S = file.readLine();		Lnostrilx = getXCoord( S, 6 );		Lnostrily = getYCoord( S, 6 );		S = file.readLine();		Rnostrilx = getXCoord( S, 6 );		Rnostrily = getYCoord( S, 6 );			S = file.readLine();		TLeyebrowx = getXCoord( S, 6 );		TLeyebrowy = getYCoord( S, 6 );		S = file.readLine();		TReyebrowx = getXCoord( S, 6 );		TReyebrowy = getYCoord( S, 6 );		S = file.readLine();		BLeyebrowx = getXCoord( S, 4 );		BLeyebrowy = getYCoord( S, 4 );		S = file.readLine();		BReyebrowx = getXCoord( S, 4 );		BReyebrowy = getYCoord( S, 4 );			S = file.readLine();		Tupperlipx = getXCoord( S, 7 );		Tupperlipy = getYCoord( S, 7 );		S = file.readLine();		Bupperlipx = getXCoord( S, 7 );		Bupperlipy = getYCoord( S, 7 );				S = file.readLine();		Tlowerlipx = getXCoord( S, 7 );		Tlowerlipy = getYCoord( S, 7 );		S = file.readLine();		Blowerlipx = getXCoord( S, 7 );		Blowerlipy = getYCoord( S, 7 );			S = file.readLine();		Lfacex = getXCoord( S, 3 );		Lfacey = getYCoord( S, 3 );		S = file.readLine();		Rfacex = getXCoord( S, 3 );		Rfacey = getYCoord( S, 3 );					S = file.readLine();		Learx = getXCoord( S, 7 );		Leary = getYCoord( S, 7 );		S = file.readLine();		Rearx = getXCoord( S, 7 );		Reary = getYCoord( S, 7 );					S = file.readLine();		jawx = getXCoord( S, 11 );		jawy = getYCoord( S, 11 );		S = file.readLine();		hairlinex = getXCoord( S, 13 );		hairliney = getYCoord( S, 13 );		S = file.readLine();		topofheadx = getXCoord( S, 13 );		topofheady = getYCoord( S, 13 );				S = file.readLine();		Lcheeklinex = getXCoord( S, 3 );		Lcheekliney = getYCoord( S, 3 );		S = file.readLine();		Rcheeklinex = getXCoord( S, 3 );		Rcheekliney = getYCoord( S, 3 );					S = file.readLine();		Lcheekbonex = getXCoord( S, 3 );		Lcheekboney = getYCoord( S, 3 );		S = file.readLine();		Rcheekbonex = getXCoord( S, 3 );		Rcheekboney = getYCoord( S, 3 );			S = file.readLine();		Lupperliplinex = getXCoord( S, 2 );		Lupperlipliney = getYCoord( S, 2 );		S = file.readLine();		Rupperliplinex = getXCoord( S, 2 );		Rupperlipliney = getYCoord( S, 2 );					S = file.readLine();		chincleftx = getXCoord( S, 2 );		chinclefty = getYCoord( S, 2 );					S = file.readLine();		chinlinex = getXCoord( S, 3 );		chinliney = getYCoord( S, 3 );	}				private int[] getXCoord( String Coord, int pts ) { 				String S = "";		StringTokenizer Xtoken = new StringTokenizer( Coord );		int[] Xarray = new int[pts];		try {			for( int i=0; i<pts; i++ ) {				S = Xtoken.nextToken();				Xarray[i] = (Integer.parseInt( S ))/2;				Xtoken.nextToken();			}		}		catch( Exception e ) { System.out.println("Bad get X Coord!!"); }		return Xarray;	}		private int[] getYCoord( String Coord, int pts ) { 		StringTokenizer Ytoken = new StringTokenizer( Coord );		int[] Yarray = new int[pts];		try {			for( int i=0; i<pts; i++ ) {				Ytoken.nextToken();				Yarray[i] = (Integer.parseInt( Ytoken.nextToken()) ) / 2 +15;			}		}		catch ( Exception e ) { System.out.println("Bad get Y Coord!!"); }		return Yarray;	}				public void drawFace( Graphics g ) {			g.drawPolyline( Lpupilx, Lpupily, 2 );				// left pupil 		g.drawPolyline( Rpupilx, Rpupily, 2 );				// right pupil		g.drawPolyline( Lirisx, Lirisy, 5 );				// left iris 		g.drawPolyline( Ririsx, Ririsy, 5 ); 				// right iris 		g.drawPolyline( BLeyelidx, BLeyelidy, 3 );			// bottom of left eyelid		g.drawPolyline( BReyelidx, BReyelidy, 3 );			// bottom of right eyelid		g.drawPolyline( BLeyex, BLeyey, 3 );				// bottom of left eye		g.drawPolyline( BReyex, BReyey, 3 );				// bottom of right eye		g.drawPolyline( TLeyex, TLeyey, 3 );				// top of left eye		g.drawPolyline( TReyex, TReyey, 3 );				// top of right eye		g.drawPolyline( Leyelinex, Leyeliney, 3 );			// left eye line		g.drawPolyline( Reyelinex, Reyeliney, 3 );			// right eye line		g.drawPolyline( Lnosex, Lnosey, 6 );				// left side of nose		g.drawPolyline( Rnosex, Rnosey, 6 );				// right side of nose		g.drawPolyline( Lnostrilx, Lnostrily, 6 );			// left nostril		g.drawPolyline( Rnostrilx, Rnostrily, 6 );			// right nostril		g.drawPolyline( TLeyebrowx, TLeyebrowy, 6 );			// top of left eyebrow		g.drawPolyline( TReyebrowx, TReyebrowy, 6 );			// top of right eyebrow		g.drawPolyline( BLeyebrowx, BLeyebrowy, 4 );			// bottom of left eyebrow 		g.drawPolyline( BReyebrowx, BReyebrowy, 4 );			// bottom of right eyebrow		g.drawPolyline( Tupperlipx, Tupperlipy, 7 );			// top of upper lip		g.drawPolyline( Bupperlipx, Bupperlipy, 7 );			// bottom of upper lip 		g.drawPolyline( Tlowerlipx, Tlowerlipy, 7 );			// top of lower lip		g.drawPolyline( Blowerlipx, Blowerlipy, 7 );			// bottom of lowerlip	 		g.drawPolyline( Lfacex, Lfacey, 3 );				// left side of face		g.drawPolyline( Rfacex, Rfacey, 3 );				// right side of face		g.drawPolyline( Learx, Leary, 7 );					// left ear		g.drawPolyline( Rearx, Reary, 7 );					// right ear		g.drawPolyline( jawx, jawy, 11 );					// jaw 		g.drawPolyline( hairlinex, hairliney, 13 );			// hairline 		g.drawPolyline( topofheadx, topofheady, 13 );		// top of head 		g.drawPolyline( Lcheeklinex, Lcheekliney, 3 );		// left cheek line		g.drawPolyline( Rcheeklinex, Rcheekliney, 3 );		// right cheek line		g.drawPolyline( Lcheekbonex, Lcheekboney, 3 );		// left cheekbone 		g.drawPolyline( Rcheekbonex, Rcheekboney, 3 );		// right cheekbone		g.drawPolyline( Lupperliplinex, Lupperlipliney, 2 );	// left upper lip line		g.drawPolyline( Rupperliplinex, Rupperlipliney, 2 );	// right upper lip line		g.drawPolyline( chincleftx,  chinclefty, 2 );		// chin cleft 							// left upper lip line		g.drawPolyline( chinlinex, chinliney, 3 );			// chin line 	}}