// ********************** PUBLIC OPERATIONS ******************************// Construction: none // public void init()			~ initilizes the Applet.// public void start()		~ starts the thread to display the screen saver.// public void stop()			~ stops the thread displaying the screen saver.// public void run()			~ includes a sleep command to control the speed.import java.applet.Applet;import java.awt.*;import java.lang.Thread;public class FaceMutator extends Applet implements Runnable {		Thread runner;	Image offscreenImage, oldFaceImage, newFaceImage, morphedImage;	Graphics offscreenG, newFaceG, oldFaceG, morphedG;	int oldFile = Face.STANDARD; 	int newFile =  Face.NEW; 		Face oldFace;	Face newFace;	Morpher morph;		public void init() {				this.setLayout( null );		this.setSize( 500, 500 );		this.setBackground( Color.white );				offscreenImage = createImage( this.getBounds().width, this.getBounds().height );		morphedImage = createImage( 115, 110);		oldFaceImage = createImage( 125, 140 );		newFaceImage = createImage( 125, 140 );		offscreenG = offscreenImage.getGraphics();		morphedG = morphedImage.getGraphics();		oldFaceG = oldFaceImage.getGraphics();		newFaceG = newFaceImage.getGraphics();				oldFace = new Face( oldFile );		newFace = new Face( newFile );				oldFace.drawFace( oldFaceG );		newFace.drawFace( newFaceG );				// put the static faces in the main area 		offscreenG.drawImage( oldFaceImage, 0, -30, Color.black, this );//-25		offscreenG.drawImage( newFaceImage, 160, -30, Color.black, this );//135		offscreenG.setFont( new Font( "SansSerif", Font.PLAIN, 9 ) );		offscreenG.drawString( "Shikhar,Rishabh,Rachit", this.getBounds().width - 92, this.getBounds().height - 5);				morph = new Morpher( oldFace, newFace );				start();	}		public void start() {				if (runner == null) {			runner = new Thread(this);			runner.start();		}	}		public void stop() {				if (runner != null) {			runner.stop();			runner = null;		}	}		public void run() {				for(;;) {			try { Thread.sleep(125); }			catch ( InterruptedException e ) {}			repaint();		}	}/** override the update to prevent the screen from re-drawing*/	public void update( Graphics g ) {		paint(g);	}		/**In the paint method the Face objects are first drawn to the offscreenG graphics object and then this object is drawn to the Applet.*/ 	public void paint( Graphics g ) {				// draw a bounding box		offscreenG.setColor( Color.black );		offscreenG.drawLine( 0,0,this.getBounds().width-1,0 );		offscreenG.drawLine( this.getBounds().width-1,0, this.getBounds().width-1, this.getBounds().height-1 );		offscreenG.drawLine( this.getBounds().width-1, this.getBounds().height-1, 0,this.getBounds().height-1 );		offscreenG.drawLine( 0,this.getBounds().height-1,0,0 );						morphedG.setColor( Color.black );		morph.drawFace( morphedG );				// put the animated image in the main graphics area		offscreenG.drawImage( morphedImage, 105,100, Color.black, this );//85				g.drawImage( offscreenImage, 0, 0, Color.black, this ); // draw to screen				morphedG.setColor( Color.white );	// 'erase' the morphing image 		morph.drawFace( morphedG );				morph.increment();				// advance the animation	}}						